import { Component, OnInit } from '@angular/core';
import { ActivatedRoute, Router } from '@angular/router';
import { Game } from 'src/app/models/game.model';
import { GamesService } from 'src/app/services/games.service';

@Component({
  selector: 'app-edit-game',
  templateUrl: './edit-game.component.html',
  styleUrls: ['./edit-game.component.css']
})
export class EditGameComponent implements OnInit {

  gameDetails: Game = {
    id: '',
    name: '',
    description: '',
    platform: '',
    img: ''
  };

  constructor(private route: ActivatedRoute, private gameService: GamesService, private router: Router) {}

  ngOnInit(): void {
    this.route.paramMap.subscribe({
      next: (params) => {
        const id = params.get('id');
        if (id) {
          this.gameService.getGame(id)
          .subscribe({
            next: (response) => {
              this.gameDetails = response;
            }

          });
        }
      }
    })

  }

  updateGame(){
    this.gameService.updateGame(this.gameDetails.id, this.gameDetails)
    .subscribe({
      next: (response) => {
        this.router.navigate(['games']);

      }
    })
  }

  deleteGame(id: string){
    this.gameService.deleteGame(id)
    .subscribe({
      next: (response) => {
        this.router.navigate
      }
    })

  }

}
<div class="container my-5">
  <h1 class="mb-3">Edit Game</h1>

  <div class="row">
    <div class="col-6">
      <form #form="ngForm" (ngSubmit)="updateGame()">
        <div class="mb-3">
          <label for="id" class="form-label">Id</label>
          <input type="text" class="form-control" id="id" readonly name="id" [(ngModel)]="gameDetails.id">
        </div>
        <div class="mb-3">
          <label for="name" class="form-label">Name</label>
          <input type="text" class="form-control" id="name" name="name" [(ngModel)]="gameDetails.name">
        </div>
        <div class="mb-3">
          <label for="description" class="form-label">Description</label>
          <input type="text" class="form-control" id="description" name="description" [(ngModel)]="gameDetails.description">
        </div>
        <div class="mb-3">
          <label for="platform" class="form-label">Platform</label>
          <input type="text" class="form-control" id="platform" name="platform" [(ngModel)]="gameDetails.platform">
        </div>
        <div class="mb-3">
          <label for="img" class="form-label">ImgUrl</label>
          <input type="text" class="form-control" id="img" name="img" [(ngModel)]="gameDetails.img">
        </div>
        <button type="submit" class="btn btn-primary">Save</button>
      </form>
    </div>
  </div>

</div>
