import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { GamesService } from 'src/app/services/games.service';

@Component({
  selector: 'app-edit-game',
  templateUrl: './edit-game.component.html',
  styleUrls: ['./edit-game.component.css']
})
export class EditGameComponent implements OnInit {



  constructor(private route: ActivatedRoute, private gameService: GamesService) {}

  ngOnInit() : void{
    this.route.paramMap.subscribe({
      next: (params) => {
        const id = params.get('id');

        if (id) {
          this.gameService.getGame(id)
          .subscribe({
            next: (response) => {
              this.gameService
            }

          });
        }
      }
    })

  }

}
<div class="container my-5">
  <h1 class="mb-3">Edit Game</h1>

  <div class="row">
    <div class="col-6">
      <form #form="ngForm">
        <div class="mb-3">
          <label for="id" class="form-label">Id</label>
          <input type="text" class="form-control" id="id" readonly name="id" [(ngModel)]="addGameRequest.name">
        </div>
        <div class="mb-3">
          <label for="name" class="form-label">Name</label>
          <input type="text" class="form-control" id="name" name="name" [(ngModel)]="addGameRequest.name">
        </div>
        <div class="mb-3">
          <label for="description" class="form-label">Description</label>
          <input type="text" class="form-control" id="description" name="description" [(ngModel)]="addGameRequest.description">
        </div>
        <div class="mb-3">
          <label for="platform" class="form-label">Platform</label>
          <input type="text" class="form-control" id="platform" name="platform" [(ngModel)]="addGameRequest.platform">
        </div>
        <div class="mb-3">
          <label for="imgurl" class="form-label">ImgUrl</label>
          <input type="text" class="form-control" id="imgurl" name="imgurl" [(ngModel)]="addGameRequest.imgurl">
        </div>
        <button type="submit" class="btn btn-primary">Save</button>
      </form>
    </div>
  </div>

</div>
