import { Component, OnInit } from '@angular/core';
import { Game } from 'src/app/models/game.model';
import { GamesService } from 'src/app/services/games.service';

@Component({
  selector: 'app-game-cards',
  templateUrl: './game-cards.component.html',
  styleUrls: ['./game-cards.component.css']
})
export class GameCardsComponent implements OnInit {

  games: Game[] = [];
  constructor(private gamesServices : GamesService) { }

  ngOnInit(): void {
    this.gamesServices.getAllGames()
    .subscribe({
      next: (games) => {
        this.games = games;
      },
      error: (response) => {
        console.log(response);
      }
    });


  }

}
<div class="container">
  <div class="row">
      @foreach (var hotel in Model)
      <div class="col-4">
          <div class="card" style="width: 18rem;">
              <img src= "@hotel.ImgURL"
                  class="card-img-top" alt="...">
                  <a  class="btn btn-danger" asp-controller="Hotel" asp-action="DeleteHotel" asp-route-id="@hotel.Id">X</a>
              <div class="card-body">
                  <h5 class="card-title">@hotel.Name</h5>
                  @for (int i = 0; i < @hotel.Rating; i++
                      <span class="fa fa-star checked"></span>
                  }
                  <p class="card-text">Location: @hotel.Location</p>
              </div>
              <div class="card-body">
                  <a asp-controller="Hotel" asp-action="GetOneHotel" asp-route-id="@hotel.Id" class="btn btn-primary">View</a>
              </div>
          </div>
      </div>
  </div>
</div>
